define("application", function () {    var application = {};    application.init = function () {        requirejs(['jquery', 'bootstrap-switch', 'matchHeight'], function ($) {            $(".navbar-expand-toggle").click(function () {                $(".app-container").toggleClass("expanded");                $(".navbar-expand-toggle").toggleClass("fa-rotate-90");            });            $(".navbar-right-expand-toggle").click(function () {                $(".navbar-right").toggleClass("expanded");                $(".navbar-right-expand-toggle").toggleClass("fa-rotate-90");            });            $('.toggle-checkbox').bootstrapSwitch({                size: "small"            });            $(".sidebar-toggle").bind("click", function (e) {                $("#sidebar").toggleClass("active");                $(".app-container").toggleClass("__sidebar");            });            $(".navbar-toggle").bind("click", function (e) {                $("#navbar").toggleClass("active");                $(".app-container").toggleClass("__navbar");            });            $(".side-menu .nav .dropdown").on('show.bs.collapse', function () {                $(".side-menu .nav .dropdown .collapse").collapse('hide');            });        });    };    return application;});